<?php header('Content-Type: application/json; charset=utf-8'); require_once __DIR__.'/db.php'; $loc = $_GET['location'] ?? null; $m = $_GET['month'] ?? null; $q = 'SELECT * FROM weather_data'; $params=[]; if($loc){ $q.=' WHERE location = ?'; $params[]=$loc; if($m){ $q.=' AND month = ?'; $params[]=$m; } } $stmt = $pdo->prepare($q); $stmt->execute($params); $d=$stmt->fetchAll(); echo json_encode(['success'=>true,'data'=>$d]); ?>